mapper.select:
select count(*) note_times,TO_CHAR(OL.OPERATE_TIME,'yyyy-mm') note_date from OPERATE_LOG OL LEFT JOIN AD_EMPLOYEE_V AEV ON OL.USER_ID = AEV.HR_NO where OL.operate_type = '2' and AEV.DEPARTID in ( select distinct(departid) from ad_depart_v Start with departid=#{site} Connect by prior depart_code=PARENTCODE)
and TO_CHAR(OL.OPERATE_TIME,'yyyy-mm') <= #{endDate}
and TO_CHAR(OL.OPERATE_TIME,'yyyy-mm') >= #{staDate}
GROUP BY TO_CHAR(OL.OPERATE_TIME,'yyyy-mm')

select * from (
select * from OPERATE_LOG where 1=1

    and user_id= #{userId}


    and operate_type= #{operateType}

order by OPERATE_TIME desc)
where rownum <= 2
