mapper.select:
select count(*) note_times,TO_CHAR(OPERATE_TIME,'yyyy-mm') note_date
from OPERATE_LOG
where operate_type = '2'

    and TO_CHAR(OPERATE_TIME,'yyyy-mm') <= #{endDate}


    and TO_CHAR(OPERATE_TIME,'yyyy-mm') >= #{staDate}

GROUP BY TO_CHAR(OPERATE_TIME,'yyyy-mm')

select * from (
select * from OPERATE_LOG where 1=1

    and user_id= #{userId}


    and operate_type= #{operateType}

order by OPERATE_TIME desc)
where rownum <= 2
